import React, { useEffect, useState } from 'react';
import { room } from '../interfaces/Room';
import { AgesForm } from './AgesForm';

type Field = 'adultCount' | 'childCount';

interface GuestFormProps {
    onSendRoomData: (rooms: Map<roomNumber, room>) => void;
}
interface roomNumber {
    id: number;
}
export const GuestForm: React.FC<GuestFormProps> = ({ onSendRoomData }) => {
    const initialRooms = new Map<roomNumber, room>();
    initialRooms.set({ id: initialRooms.size + 1 }, { adultCount: 1, child: { childCount: 0, ages: [] } });
    const [rooms, setRooms] = useState<Map<roomNumber, room>>(initialRooms);

    const handleAddRoom = () => {
        const newRoom: room = {
            adultCount: 1,

            child: {
                childCount: 0,
                ages: []
            }
        };
        setRooms(prevRooms => {
            const newRooms = new Map(prevRooms);
            newRooms.set({ id: prevRooms.size + 1 }, newRoom);
            return newRooms
        })
    };

    useEffect(() => {
        onSendRoomData(rooms);
    }, [rooms, onSendRoomData]);

    console.log(rooms, 'From Rooms');

    const handleAgesChange = (roomId: number, newAges: number[]) => {
        setRooms((prevRooms) => {
            const newRooms = new Map(prevRooms);
            const room = newRooms.get({ id: roomId });
            if (room) {
                newRooms.set({ id: roomId }, {
                    ...room,
                    child: {
                        ...room.child,
                        ages: newAges
                    }
                });
            }
            return newRooms;
        });
    };
    const handleDeleteRoom = (roomId: number) => {
        setRooms(prevRooms => {
            const newRooms = new Map(prevRooms);
            newRooms.delete({ id: roomId });
            return newRooms
        });
    };

    const handleRoomGuestChange = (roomId: number, field: Field, value: number) => {
        setRooms(prevRooms => {
            const newRooms = new Map(prevRooms);
            const room = newRooms.get({ id: roomId })
            if (room) {
                if (field === "adultCount") {
                    newRooms.set({ id: roomId }, {
                        ...room,
                        adultCount: value
                    })
                } else if (field === "childCount") {
                    newRooms.set({ id: roomId }, {
                        ...room,
                        child: {
                            ...room.child,
                            childCount: value
                        }
                    })
                }

            }
            return newRooms;
        });
    };

    return (
        <div className='GuestForm'>
            {rooms.entries().map(([{id:roomId}, room]) => (
                <form key={roomId}>
                    <input
                        type="number"
                        value={room.adultCount}
                        onChange={(e) => handleRoomGuestChange(roomId, 'adultCount', Number(e.target.value))}
                    />
                    <input
                        type="number"
                        value={room.child.childCount}
                        onChange={(e) => handleRoomGuestChange(roomId, 'childCount', Number(e.target.value))}
                    />
                    {room.child.childCount > 0 && (
                        <AgesForm
                            childCount={room.child.childCount}
                            onChildrenAges={(newChildAges) => handleAgesChange(roomId, newChildAges)}
                        />
                    )}
                    {roomId > 1 && (
                        <button type='button' onClick={() => handleDeleteRoom(roomId)}>Delete</button>
                    )}
                </form>
            ))}
            <button onClick={handleAddRoom}>addRoom</button>
        </div>
    );

};
