import React from 'react'
import { useBookingContext } from './ContexApplication';
import { GuestCart } from './GuestCart';
import { AgeCartComponent } from './AgeCartComponent';
import "../css/roomManagemantComponent.css";
import { BottonComponent } from './BottonComponent';
export const RoomManagemantComponent: React.FC = () => {
    const { bookingData, handleAddNewRoom, handleDeleteRoom,
        handleGuestCountChange, updateAgesLenght, handleNextClick
    } = useBookingContext();
    console.log(bookingData);
    return (
        <section
            className='RoomManagemantComponent'
        >
            {Array.from(bookingData.rooms.entries()).map(([roomIndex, room]) => (
                <section
                    key={roomIndex}
                    className='Guest'
                    style={{ display: "flex" }}>
                    <section >
                        <div>
                            <GuestCart
                                onGuestChange={(field, value) => handleGuestCountChange(field, roomIndex, value)}
                                key={roomIndex}
                                onChildrenCountChange={(count) => updateAgesLenght(roomIndex, count)}
                            />
                        </div>
                        <div >

                            {room.child.childCount > 0 &&
                                <AgeCartComponent
                                    childrenCount={room.child.childCount}
                                    roomIndex={roomIndex}
                                />
                            }
                        </div>
                    </section>
                    <div>
                        {roomIndex > 1 && <button onClick={() => handleDeleteRoom(roomIndex)}>delete</button>}
                    </div>
                </section>
            ))}

            <BottonComponent
                onClickButton={handleAddNewRoom}
                padding='0.5rem'
            >Add Room
            </BottonComponent>

            <BottonComponent
                onClickButton={handleNextClick}
                padding='0.5rem'
            >
                Next
            </BottonComponent>

        </section>
    )
}
