import React from 'react'
import { date } from '../interfaces/BookedDate'
import "../css/dataForm.css"
interface DateFormProps {
  date: date;
  onDateChange: (field: keyof date, value: string) => void;
}
export const DateForm: React.FC<DateFormProps> = ({ date, onDateChange }) => {

  return (
    <div className='Date' style={{backgroundColor:"#2a1a2a", width:"100vw"}}>
      <form  >
        <div className='CheckIn  DateInput'>
          <input
            type="date"
            value={date.checkIn}
            name='checkIn'
            onChange={(e) => onDateChange('checkIn', e.target.value)}
          />
        </div>
        <div className='CheckOut  DateInput'>
          <input
            type="date"
            value={date.checkOut}
            name='checkOut'
            onChange={(e) => onDateChange('checkOut', e.target.value)}
          />
        </div>
      </form>
    </div>
  )
}
