import React, { useEffect, useState } from 'react';
import { room } from '../interfaces/Room';
type field = "adultCount"| "childCount";
interface GuestFormProps{
    rooms:Map<number, room>;
    onHandleGuestCountChange:(index:number, field:field, value:number)=>void
}

export const GuestForm: React.FC<GuestFormProps> = ({ rooms, onHandleGuestCountChange }) => {

    return (
        <div className='GuestForm'>
            {Array.from(rooms.entries()).map(([index, room]) => (

               <form  key={index}>
                <input 
                    type="number"
                    name="adultCount" 
                    value={room.adultCount}
                    onChange={(e)=>onHandleGuestCountChange(index, 'adultCount', Number(e.target.value))}

                />
                <input  
                    type="number" 
                    name="childCount"
                    value={room.child.childCount}
                    onChange={(e)=>onHandleGuestCountChange(index, 'childCount', Number(e.target.value))}
                />
               </form>

            ))}

        </div>
    );
};
