import React, { useState } from 'react'
import { guest } from '../interfaces/guest'
interface UserFormProps {
    onUserChange:(guest:guest)=>void;
    onNext:()=>void;
}
export const userForm: React.FC<UserFormProps> = ({onUserChange, onNext}) => {
    const[formGuest, setFormGuest] = useState<guest>({
        name:'',
        lastName:'',
        email:'',
        phone:''
    })
    const handleSubmit = (e:React.FormEvent) =>{
        e.preventDefault();
        onUserChange(formGuest);
        onNext();
    }
    const formUserChange = (e:React.ChangeEvent<HTMLInputElement>)=>{
        const {name, value} = e.target;
        setFormGuest(prev=>({
            ...prev,
            [name]:value
        }))
    }

    return (
        <div className='userForm'>
            <form  onSubmit={handleSubmit}>
                <input
                    type="text"
                    name='name'
                    placeholder='Name'
                    value={formGuest.name}
                    onChange={formUserChange}
                    required
                />
                <input
                    type="text"
                    name='LastName'
                    placeholder='LastName'
                    value={formGuest.lastName}
                    onChange={formUserChange}
                    required
                />
                <input
                    type="email"
                    name='emael'
                    placeholder='Email'
                    value={formGuest.email}
                    onChange={formUserChange}
                />
                <input
                    type="tel"
                    name='Phone'
                    value={formGuest.phone}
                    onChange={formUserChange}
                />
                <button type='submit'></button>
            </form>
        </div>
    )
}
