import React from 'react'
import { ButtonComponent } from './ButtonComponent'
import { useBookingContext } from './ContexApplication';
import "../css/SelectedRoomHeader.css";
interface SelectedRoomHeaderProps {
    handleNext: () => void;
    onPrevious: () => void;
}
export const SelectedRoomHeader: React.FC<SelectedRoomHeaderProps> = ({ handleNext, onPrevious }) => {
    const { getGuestInfo } = useBookingContext();
    const StatGuestFormatted = getGuestInfo("stat");
    return (
        <header className='Header'>
            <div className='HeaderTitle'>
                <h1 style={{ textAlign: "center", fontSize: "2rem" }}>SELECT ROOM</h1>
            </div>
            <section className='flex'>
                <ButtonComponent onClickButton={onPrevious}>prev Page</ButtonComponent>
                <div className='GuestSelectedInformation'>
                    <label
                        htmlFor="StatGuestFormatted"
                    >GUEST SELECTED
                    </label>
                    <input type="text"
                        value={StatGuestFormatted}
                        style={{
                            fontWeight: "bold", padding: "1em",
                            minWidth: "20rem", backgroundColor: "rgb(92, 92, 92)"
                            , color: "#fff"
                        }}
                        readOnly
                    />
                </div>
                <ButtonComponent
                    onClickButton={handleNext}
                    padding="1em"
                >Next
                </ButtonComponent>
            </section>
        </header>
    )
}
