import React, { useEffect} from 'react';
import { useBookingContext } from './ContexApplication';
import "../css/ageCartComponent.css"
interface AgeCartComponentProps {
    childrenCount: number;
    roomIndex: number;

}

export const AgeCartComponent: React.FC<AgeCartComponentProps> = ({ roomIndex, childrenCount }) => {
    const { bookingData, setBookingData, handleAgesChanges } = useBookingContext();
    //const [localAges, setLocalAges] = useState<string[]>(Array(childrenCount).fill(""));

    useEffect(() => {
        setBookingData(prev => {
            const newRooms = new Map(prev.rooms);
            const roomToUpdate = newRooms.get(roomIndex);
            if (roomToUpdate) {
                const updatedChild = {
                    ...roomToUpdate.child,
                    ages: Array(childrenCount).fill(0) // Inicializa el array con ceros o con algún valor por defecto
                };
                const updatedRoom = {
                    ...roomToUpdate,
                    child: updatedChild
                };
                newRooms.set(roomIndex, updatedRoom);
            }
            return {
                ...prev,
                rooms: newRooms
            };
        });
    }, [childrenCount, roomIndex]);

    return (
        <div className='AgeCartComponent'
        >
            {Array.from(bookingData.rooms.entries()).map(([roomIndex, room])=>(
                <div>
                    {room.child.ages.map((value, index)=>(
                        <select
                        key={index}
                        value={value}
                        onChange={(e) => handleAgesChanges(roomIndex, index, Number(e.target.value))}
                    >
                        <option value="">
                            --Select child {index + 1} Age--
                        </option>
                        {[...Array(18)].map((value, optionIndex) => (
                            <option
                                key={optionIndex}
                                value={value}
                            >
                                {optionIndex} years old
                            </option>
                        ))}
                    </select>
                    ))}
                </div>
            ))}
        </div>
    );
};
