import React, { useState } from 'react';
import { BookingForm } from './pages/BookingForm';
import { resevationData } from './interfaces/ReservationData';
//import { Room } from './interfaces/Room';
import { guest } from './interfaces/guest';
import { UserForm } from './pages/UserForm';
import { SelectedRooms } from './pages/SelectedRooms';
import { Data } from './interfaces/Data';
import { DATA } from './data/DATA';
import './css/app.css';
export const App: React.FC = () => {
  const [bookingData, setBookingData] = useState<resevationData>()
  const [nextPage, setNextPage] = useState<number>(1);
  const handleNextPage = () => {
    setNextPage(prev => prev + 1);
  }
  const handlePrevoiusPage =()=>{
    setNextPage(prev=> prev -  1);
  }
  const handleRoomsChange = (data: resevationData) => {
    console.log(data);
  }
  const handleFormChange = (user: guest) => {
    console.log(user);
  }
  const handleSelectedRoomsChange = (data: Data[]) => {
    console.log(data);
  }
  const max = 1;
  const rederPage = () => {
    switch (nextPage) {
      case 1:
        return <BookingForm
          onDataChange={handleRoomsChange}
          onNext={handleNextPage} />
      case 2:
        return <SelectedRooms
          data={DATA}
          onSelectedRoomsChange={handleSelectedRoomsChange}
          totalGuest={max}
          onNext={handleNextPage}
          onPrevious={handlePrevoiusPage}
        />
      case 3:
        return <UserForm
          onUserChange={handleFormChange}
          onNext={handleNextPage} 
          onPrevious={handleNextPage}
          />
      default:
        return <div>Thank you for your reservation!</div>
    }
  }

  return (
    <div className="App">
      {rederPage()}
    </div>
  );
}
