import React from 'react';
import { useBookingContext } from './ContexApplication';
import { date } from '../interfaces/BookedDate';

export const DateComponent = () => {
  const { bookingData, setBookingData } = useBookingContext();

  const handleDateUpdate = (field: keyof date, e: React.ChangeEvent<HTMLInputElement>) => {
    const newDate = new Date(e.target.value);
    setBookingData(prev => ({
      ...prev,
      date: {
        ...prev.date,
        [field]: newDate,
      },
    }));
  };

  return (
    <section className='DateComponent'>
      <input
        type="date"
        value={bookingData.date.checkIn.toISOString().split('T')[0]}
        name='CheckIn'
        onChange={(e) => handleDateUpdate('checkIn', e)}
      />
      <input
        type="date"
        value={bookingData.date.checkOut.toISOString().split('T')[0]}
        name='CheckOut'
        onChange={(e) => handleDateUpdate('checkOut', e)}
      />
    </section>
  );
};
