import React, { useState } from 'react';
import { DateComponent } from './DateComponent';
import { RoomManagemantComponent } from './RoomManagemantComponent';
import { ShowRoomInfoComponent } from './ShowRoomInfoComponent';
import "../css/bookingComponent.css";
import { ButtonComponent } from './ButtonComponent';
interface bookingComponentProps {
    onNext: () => void;
}
export const BookingComponent: React.FC<bookingComponentProps> = ({ onNext }) => {
    const [inputClicked, setInputClicked] = useState<boolean>(false);
    const handleClickedInput = (inputClick: boolean) => {
        setInputClicked(inputClick);
    }
    return (
        <main className='BookingHeader  flex'>
            <div className='BookingHeaderInner ' style={{display:"flex"}}>

                <div className='DataDisplay'>
                    <DateComponent />
                </div>
                <div className='RoomInfoComponent'>
                    <ShowRoomInfoComponent
                        onClickedInput={handleClickedInput}
                    />
                    {inputClicked && <RoomManagemantComponent />}
                </div>
                <ButtonComponent
                    onClickButton={onNext}
                    padding='0.9rem'
                    minWidth='10rem'
                >Next
                </ButtonComponent>
            </div>

        </main>
    )
}
