import React from 'react'
import { Room } from '../interfaces/Room'
type field = 'adultCount'|'childCount'
interface GuestInputProps{
    room:Room;
    onHandleGuestChange:(field:field, value:number)=>void;
}
export const GuestInput:React.FC<GuestInputProps> = ({room, onHandleGuestChange}) => {
    return (
        <div className='GuestInput'>
            <input
                type="number"
                name="adultCount"
                value={room.adultCount}
                onChange={(e) => onHandleGuestChange('adultCount', Number(e.target.value))}
                //style={{ marginTop: "0.2em", maxWidth: "10em", textAlign: "center" }}
            />
            <input
                type="number"
                name="childCount"
                value={room.child.childCount}
                onChange={(e) =>onHandleGuestChange('childCount', Number(e.target.value))}
                //style={{ marginTop: "0.2em", maxWidth: "10em", textAlign: "center" }}
            />
        </div>
    )
}
