import React, { useEffect} from 'react';
import { useBookingContext } from './ContexApplication';
import "../css/ageCartComponent.css"
import { Room } from '../interfaces/Room';
interface AgeCartComponentProps {
    childrenCount: number;
    roomIndex: number;
    room:Room
}
export const AgeCartComponent: React.FC<AgeCartComponentProps> = ({room, roomIndex, childrenCount }) => {
    const { bookingData, setBookingData, handleAgesChanges} = useBookingContext();

    useEffect(() => {
        setBookingData(prev => {
            const newRooms = new Map(prev.rooms);
            const roomToUpdate = newRooms.get(roomIndex);
            if (roomToUpdate) {
                const currentAges = roomToUpdate.child.ages || [];
                const updatedAges = [...currentAges];
                // Asegurarse de que el tamaño del array de edades coincida con childrenCount
                while (updatedAges.length < childrenCount) {
                    updatedAges.push("");
                }
                // Si el número de niños disminuye, ajustar el array de edades
                if (updatedAges.length > childrenCount) {
                    updatedAges.splice(childrenCount);
                }
                const updatedChild = {
                    ...roomToUpdate.child,
                    ages: updatedAges
                };
                const updatedRoom = {
                    ...roomToUpdate,
                    child: updatedChild
                };
                newRooms.set(roomIndex, updatedRoom);
            }
            return {
                ...prev,
                rooms: newRooms
            };
        });
    }, [childrenCount, roomIndex, setBookingData]);

    //const room = bookingData.rooms.get(roomIndex);

    return (
        <div className='AgeCartComponent'>
            {room.child.ages.map((value, index) => (
                <select
                    key={index}
                    value={value}
                    onChange={(e) => handleAgesChanges(roomIndex, index, Number(e.target.value))}
                >
                    <option value="">
                        --Select child {index + 1} Age--
                    </option>
                    {[...Array(18)].map((_, optionIndex) => (
                        <option
                            key={optionIndex}
                            value={optionIndex}
                        >
                            {optionIndex} years old
                        </option>
                    ))}
                </select>
            ))}
        </div>
    );
};
